export default function TextArea(props) {
    const convertToUpper=()=>{
        let newText=text.toUpperCase()
        setText(newText)
    }

    const editText=()=>{
        setText()
    }

    const[text,setText]=useState("Enter your text here")
    return (
        <div>
            <div className="container">
                <form>
                    <h1>{props.heading}</h1>
                    <div classNameName="mb-3">
                        <textarea className='form-control' id="my-textbox" cols="30" rows="10" onChange={editText}></textarea>
                    </div>
                    <button className="btn btn-primary" onClick={convertToUpper}>Convert</button>
                </form>
            </div>
        </div>
    )
}
