import React, { useState } from 'react'

export default function TextArea(props) {
    const convertToUpper=()=>{
        let newText=text.toUpperCase()
        setText(newText)
    }
    const convertToLower=()=>{
        let newText=text.toLowerCase()
        setText(newText)
    }
    const handleClearText =()=>{
        let newText=""
        setText(newText)
    }
    
    const editText=(event)=>{
        console.log("Changed")
        setText(event.target.value)
    }
    const[text,setText]=useState("")
    
    return (
    <>
        <div className='container'> 
            <h1>{props.heading}</h1>
                    <div className="mb-3">
                        <textarea className='form-control' id="my-textbox" value={text} cols="30" rows="10" onChange={editText} placeholder='Enter your text here'></textarea>
                    </div>
                    <button className="btn btn-primary" onClick={convertToUpper}>To UpperCase</button>
                    <button className="btn btn-primary mx-4" onClick={convertToLower}>To LowerCase</button>
                    <button className="btn btn-primary mx-4" onClick={convertToLower}>To LowerC</button>
                    <button className="btn btn-primary mx-4" onClick={handleClearText}>Clear Text</button>
        </div>
        <div className="container my-4">
            <h2>Counting Area</h2>
            <br />
            <p>Your text contains {text.length} charectors and {text.split(" ").length} Words</p>

            <h2>Preview</h2>
            <p>{text}</p>
        </div>
    </>
    )
}
