import React, { useState } from 'react'

export default function TextArea(props) {
    const convertToUpper=()=>{
        console.log(text)
        let newText=text.toUpperCase()
        setText(newText)
    }
    
    const editText=(event)=>{
        console.log("Changed")
        setText(event.target.value)
    }
    const[text,setText]=useState("")
    
    return (
        <div>
            <h1>{props.heading}</h1>
                    <div className="mb-3">
                        <textarea className='form-control' id="my-textbox" value={text} cols="30" rows="10" onChange={editText} placeholder='Enter you'></textarea>
                    </div>
                    <button className="btn btn-primary" onClick={convertToUpper}>Convert</button>
        </div>
    )
}
